function confirmarPago(id_pedido) {
    console.log(id_pedido);
    modalL = new bootstrap.Modal(document.getElementById('loading'), {
        keyboard: false
    });
    modalL.show();
    $.ajax({
        url: "/admin/confirmarPagoPedido",
        type: "post",
        data: {
            id_pedido
        },
        success: function (response) {
            setTimeout(() => {
                modalL.hide();
            }, "1000");
            console.log('===>');
            console.log(response);
            if (response.ok) {
                $('#msg-confirmado').text(response.msg)
                modalC = new bootstrap.Modal(document.getElementById('confirmado'), {
                    keyboard: false
                })
                modalC.show();
                //$.NotificationApp.send("Bien hecho!", response.msg, "bottom-right", "rgba(0,0,0,0.2)", "success");
            } else {
                $.NotificationApp.send("Aviso!", response.msg, "bottom-right", "rgba(0,0,0,0.2)", "warning");
            }
        },
        error: function (jqXHR, textStatus, errorThrown) {
            modalL.hide();
            console.log(textStatus, errorThrown);
            $.NotificationApp.send("Aviso!", errorThrown, "bottom-right", "rgba(0,0,0,0.2)", "warning");
        }
    });
}